import  CartActionTypes  from './cart.type';

const INITIAL_STATE = {
    hidden: true,
    cartItems: []
}

const cartReducer = ( state= INITIAL_STATE, action) => {
    switch (action.type) {
        case CartActionTypes.TOGGLE_CART_HIDDEN:
            return {
                ...state,
                hidden: !state.hidden
            };
        case CartActionTypes.ADD_ITEM:
            return {

            }
        default:
            return state;
    }
}

export default cartReducer;
