const CartDropdown = () => {
    return (
            <div className='cart-dropdown'>
                <div className='cart-items'>
                  {
                    cartItems.map( cartItem => <CartItem key={cartItem.id} item={cartItem} />)
                  }
                 <CustomButton>Go to checkout</CustomButton>
