import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import { createStructuredSelector } from 'reselect';

import { auth } from '../../firebase/firebase.utils';

import { ReactComponent as Logo } from '../../assets/logo.svg';

// import './header.styles.scss';
import { HeaderContainer, LogoContainer, OptionContainer , OptionDiv, OptionLink} from './hearder.styles';


import CartIcon from '../cart-icon/cart-icon.component';
import CartDropdown from '../cart-dropdown/cart-dropdow.component';

import  { selectCardHidden} from '../../redux/cart/cart.selected';
import  {selectCurrentUser} from '../../redux/user/user.selected';

const Header = ({ currentUser , hidden}) => (
  <HeaderContainer>
    <Link className='logo-container' to='/'>
      <Logo className='logo' />
    </Link>
    <div className='options'>
      <Link className='option' to='/shop'>
        SHOP
      </Link>
      <Link className='option' to='/shop'>
        CONTACT
      </Link>
      {currentUser ? (
        <div className='option' onClick={() => auth.signOut()}>
          SIGN OUT
        </div>
      ) : (
        <Link className='option' to='/signin'>
          SIGN IN
        </Link>
      )}
      <CartIcon />
    </div>
    { hidden ? null : <CartDropdown />}

  </HeaderContainer>
);

const mapStateToProps = createStructuredSelector({
    currentUser: selectCurrentUser,
    hidden: selectCardHidden
});

export default connect(mapStateToProps, null)(Header);
